mapper.select:
select a.* from ( select r.rev_id, r.res_no review_res_no, r.rev_content, r.rev_score, r.rev_enroll_date, s.*, p.rev_photo_no, p.rev_id rev_photo_rev_id, p.rev_original_filename, p.rev_renamed_filename, (select zone_code from camp c where s.camp_id = camp_id) zone_code from review r left join reservation s on r.res_no = s.res_no left join review_photo p on r.rev_id = p.rev_id ) a where ${searchType} is not null
and a.zone_code = #{campZoneType}
order by rev_enroll_date desc

select
    count(*)
from (
    select
        a.*,
        (select zone_code from camp where a.camp_id = camp_id) zone_code
    from (
        select
            r.*,
            (select camp_id from reservation where res_no = r.res_no) camp_id
        from
            review r
    ) a
) a


		a.zone_code = #{campZoneType}
